pattern="TTTGGTATCA"
dnastring=""
d=4

# From Rosalilnd 18
def HammingDistance(DNAstring1, DNAstring2):
    mismatch=0
    for i in range(len(DNAstring1)):
        if DNAstring1[i]!=DNAstring2[i]:
            mismatch+=1
    return mismatch

