# Fixed code
def PatternOccurrenceFinder(pattern, dnastring, d):
    positions=[]
    k = len(pattern)
    for i in range(len(dnastring)-k+1):
        if HammingDistance(pattern, dnastring[i:i+k]) <= d:
            positions.append(i)
    return positions

positions=PatternOccurrenceFinder(pattern, dnastring, d)
print(*positions)
            